    fn pick_creation_info(address: &str) -> Option<(&'static str, &'static str)> {
        for (addr, contract_name, creation_code) in CREATION_ARRAY.iter() {
            if address == *addr {
                return Some((contract_name, creation_code));
            }
        }

        None
    }

    // remember to remove CBOR metadata from the creation code
    const CREATION_ARRAY: [(&str, &str, &str); 4] = [
        ("0x35Fb958109b70799a8f9Bc2a8b1Ee4cC62034193", "BearXNFTStaking", ""),
        ("0x8B3D32cf2bb4d0D16656f4c0b04Fa546274f1545", "GovernorCharlieDelegate", ""),
        ("0xDb53f47aC61FE54F456A4eb3E09832D08Dd7BEec", "PoolExercise", ""),
        ("0x71356E37e0368Bd10bFDbF41dC052fE5FA24cD05", "MainchainGatewayV2", "")
    ];
